import React from "react"

import { useParams , } from "react-router-dom"
import { useState , useEffect} from "react"
import axios from "axios"
import styles from './Detail.module.css'






const Deatil = () => {
    
     const {id} = useParams();

     const [characters , setCharacters] = useState({})


     
     useEffect(() => {
        axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
            if (data.name) {
               setCharacters(data);
            } else {
               window.alert('No hay personajes con ese ID');
            }
         });
         return setCharacters({});
     },[id]

     )





    return(
        <>
        <div className={styles.div}>{characters.name}</div>
        <div className={styles.div}>{characters.status}</div>
        <div className={styles.div}>{characters.species}</div>
        <div className={styles.div}>{characters.gender}</div>
        <div className={styles.div}>{}</div>
        <div>{characters.image}</div>
        </>
    )
   
    }
export default Deatil
