const validation = (inputs) => {
    /* Validacion Email*/ 
    const error = {}
    const regexEmail = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/ ;

    if(!regexEmail.test(inputs.email)){
          error.email = "Email vacio"
    }
    if(!inputs.email){
            error.email = "El email no puede estar vacio";
        }
    

















  return error

}

export default validation
